select.trim:

AND t1.DELETED = '0' 


AND ( t1.`TYPE` = 'story' ) 

AND T1.ID = #{id}

mapper.select:
select * from (



	 AND t1.DELETED = '0' 


	 AND ( t1.`TYPE` = 'story' ) 

