mapper.sql:
 SELECT 	( SELECT COUNT( 1 ) FROM ZT_BUG WHERE PRODUCT = t1.`ID` AND `STATUS` = 'active' AND DELETED = '0' ) AS `ACTIVEBUGCNT`, 	( SELECT COUNT( 1 ) FROM ZT_STORY WHERE PRODUCT = t1.`ID` AND `STATUS` = 'active' AND DELETED = '0' ) AS `ACTIVESTORYCNT`, 	1 AS `ASSIGNTOMEBUGCNT`, 	( SELECT COUNT( 1 ) FROM ZT_BUG WHERE PRODUCT = t1.`ID` AND DELETED = '0' ) AS `BUGCNT`, 	t1.`CODE`, 	( SELECT 	t.NAME  FROM 	zt_project t, 	zt_projectproduct t2  WHERE 	t.id = t2.project  	AND t2.product = t1.id  	LIMIT 1  	) AS `CURRPROJECT`, 	t1.`DELETED`, 	( SELECT COUNT( 1 ) FROM ZT_STORY WHERE PRODUCT = t1.`ID` AND STAGE = 'developing' AND DELETED = '0' ) AS `DEVELOPINGSTORYCNT`, 	t1.`ID`, 	t1.`NAME`, 	( SELECT COUNT( 1 ) FROM ZT_RELEASE WHERE PRODUCT = t1.`ID` AND `STATUS` = 'normal' AND DELETED = '0' ) AS `NORMALRELEASECNT`, 	( SELECT COUNT( 1 ) FROM ZT_BUG WHERE PRODUCT = t1.`ID` AND `STATUS` <> 'closed' AND DELETED = '0' ) AS `NOTCLOSEDBUGCNT`, 	( SELECT COUNT( 1 ) FROM ZT_STORY WHERE PRODUCT = t1.`ID` AND STAGE = 'planned' AND DELETED = '0' ) AS `PLANNEDSTORYCNT`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_PROJECT t2 	LEFT JOIN ZT_PROJECTPRODUCT t ON t2.ID = t.project  WHERE 	t.PRODUCT = t1.id  	AND t2.DELETED = '0'  	AND t2.`status` IN ( 'wait', 'doing' )  	AND t2.`END` < now( )  	) AS `POSTPONEDPROJECTCNT`, 	( SELECT COUNT( 1 ) FROM ZT_PRODUCTPLAN WHERE PRODUCT = t1.`ID` AND DELETED = '0' ) AS `PRODUCTPLANCNT`, 	( SELECT COUNT( 1 ) FROM ZT_RELEASE WHERE PRODUCT = t1.`ID` AND DELETED = '0' ) AS `RELEASECNT`, 	( SELECT COUNT( 1 ) FROM ZT_STORY WHERE PRODUCT = t1.`ID` AND STAGE = 'released' AND DELETED = '0' ) AS `RELEASEDSTORYCNT`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_PROJECT 	LEFT JOIN ZT_PROJECTPRODUCT ON ZT_PROJECT.ID = ZT_PROJECTPRODUCT.project  WHERE 	PRODUCT = t1.`ID`  	AND DELETED = '0'  	) AS `RESPROJECTCNT`, 	t1.`STATUS`, 	( SELECT COUNT( 1 ) FROM ZT_STORY WHERE PRODUCT = t1.`ID` AND DELETED = '0' ) AS `STORYCNT`, 	( SELECT COUNT( 1 ) FROM ZT_STORY WHERE PRODUCT = t1.`ID` AND STAGE = 'testing' AND DELETED = '0' ) AS `TESTINGSTORYCNT`, 	t1.`TYPE`, 	( SELECT COUNT( 1 ) FROM ZT_BUG WHERE PRODUCT = t1.`ID` AND `CONFIRMED` = 0 AND DELETED = '0' ) AS `UNCONFIRMBUGCNT`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_PROJECT 	LEFT JOIN ZT_PROJECTPRODUCT ON ZT_PROJECT.ID = ZT_PROJECTPRODUCT.project  WHERE 	PRODUCT = t1.`ID`  	AND `STATUS` <> 'closed'  	AND DELETED = '0'  	) AS `UNDONERESPROJECTCNT`, 	( SELECT COUNT( 1 ) FROM ZT_PRODUCTPLAN WHERE PRODUCT = t1.`ID` AND `END` > CURDATE( ) AND DELETED = '0' ) AS `UNENDPRODUCTPLANCNT`, 	( SELECT COUNT( 1 ) FROM ZT_STORY WHERE PRODUCT = t1.`ID` AND STAGE = 'wait' AND DELETED = '0' ) AS `WAITSTORYCNT`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_BUG  WHERE 	PRODUCT = t1.`ID`  	AND DELETED = '0'  	AND resolvedDate BETWEEN CONCAT( 	YEAR ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	MONTH ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	DAY ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	' 00:00:00'  	)  	AND CONCAT( 	YEAR ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	MONTH ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	DAY ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	' 23:59:59'  	)  	) AS `YESTERDAYCLOSEDBUGCNT`, 	5 AS `YESTERDAYCONFIRMBUGCNT`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_BUG  WHERE 	PRODUCT = t1.`ID`  	AND `STATUS` = 'closed'  	AND DELETED = '0'  	AND CLOSEDDATE BETWEEN CONCAT( 	YEAR ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	MONTH ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	DAY ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	' 00:00:00'  	)  	AND CONCAT( 	YEAR ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	MONTH ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	DAY ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	' 23:59:59'  	)  	) AS `YESTERDAYRESOLVEDBUGCNT` , 		(CASE WHEN T2.OBJECTORDER IS NOT NULL THEN T2.OBJECTORDER ELSE  t1.`ORDER` END) as `ORDER1`, 	(CASE WHEN T2.OBJECTORDER IS NOT NULL THEN 1 ELSE 0 END) as `ISTOP` FROM 	`zt_product` t1 	left join t_ibz_top t2 on t1.id = t2.OBJECTID and t2.type = 'product' and t2.ACCOUNT = #{srf.sessioncontext.srfloginname} 


	 AND t1.DELETED = '0' 


	 AND (t1.id in( 	SELECT t.root from zt_team t WHERE 	t.account =  #{srf.sessioncontext.srfloginname} 	AND t.type = 'product'  	  	) or t1.acl = 'open' ) 	 


	 AND t1.orgid = #{srf.sessioncontext.srforgid}
