mapper.sql:
 SELECT 	( SELECT COUNT( 1 ) FROM ZT_BUG WHERE PROJECT = t1.`ID` AND `STATUS` = 'active' AND DELETED = '0' ) AS `ACTIVEBUGCNT`, 	( SELECT COUNT( 1 ) FROM ZT_BUG WHERE PROJECT = t1.`ID` AND DELETED = '0' ) AS `BUGCNT`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_STORY  WHERE 	`STATUS` = 'closed'  	AND FIND_IN_SET ( PRODUCT, ( SELECT GROUP_CONCAT( PRODUCT ) FROM ZT_PROJECTPRODUCT WHERE PROJECT = t1.`ID` ) )  	AND DELETED = '0'  	) AS `CLOSEDSTORYCNT`, 	t1.`DELETED`, 	t1.`END`, 	( SELECT COUNT( 1 ) FROM ZT_BUG WHERE PROJECT = t1.`ID` AND `STATUS` <> 'active' AND DELETED = '0' ) AS `FINISHBUGCNT`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_TASK  WHERE 	PROJECT = t1.`ID`  	AND `STATUS` IN ( 'done', 'cancel', 'closed' )  	AND DELETED = '0'  	) AS `FINISHTASKCNT`, 	t1.`ID`, 	t1.`NAME`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_STORY 	LEFT JOIN ZT_PROJECTSTORY ON ZT_STORY.ID = ZT_PROJECTSTORY.STORY  WHERE 	stage = 'released'  	AND PROJECT = t1.id  	AND DELETED = '0'  	) AS `RELEASEDSTORYCNT`, 	t1.`STATUS`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_STORY 	LEFT JOIN ZT_PROJECTSTORY ON ZT_STORY.ID = ZT_PROJECTSTORY.STORY  WHERE 	PROJECT = t1.`ID`  	AND DELETED = '0'  	) AS `STORYCNT`, 	( SELECT COUNT( 1 ) FROM ZT_TASK WHERE PROJECT = t1.`ID` AND DELETED = '0' ) AS `TASKCNT`, 	( SELECT 	round( SUM( CONSUMED ), 0 )  FROM 	ZT_TASK  WHERE 	PROJECT = t1.`ID`  	AND DELETED = '0'  	AND ( `parent` = '' OR `parent` = '0' OR `parent` = '-1' )  	) AS `TOTALCONSUMED`, 	( SELECT 	round( SUM( ESTIMATE ), 0 )  FROM 	ZT_TASK  WHERE 	PROJECT = t1.`ID`  	AND DELETED = '0'  	AND ( `parent` = '' OR `parent` = '0' OR `parent` = '-1' )  	) AS `TOTALESTIMATE`, 	( SELECT 	round( SUM( `LEFT` ), 0 )  FROM 	ZT_TASK  WHERE 	PROJECT = t1.`ID`  	AND DELETED = '0'  	AND ( `parent` = '' OR `parent` = '0' OR `parent` = '-1' )  	) AS `TOTALLEFT`, 	( 	( SELECT 	round( SUM( `LEFT` ), 0 )  FROM 	ZT_TASK  WHERE 	PROJECT = t1.`ID`  	AND DELETED = '0'  	AND ( `parent` = '' OR `parent` = '0' OR `parent` = '-1' )  	) + ( SELECT 	round( SUM( CONSUMED ), 0 )  FROM 	ZT_TASK  WHERE 	PROJECT = t1.`ID`  	AND DELETED = '0'  	AND ( `parent` = '' OR `parent` = '0' OR `parent` = '-1' )  	)  	) AS `TOTALWH`, 	( SELECT COUNT( 1 ) FROM ZT_BUG WHERE PROJECT = t1.`ID` AND `STATUS` <> 'closed' AND DELETED = '0' ) AS `UNCLOSEDBUGCNT`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_STORY 	LEFT JOIN ZT_PROJECTSTORY ON ZT_STORY.ID = ZT_PROJECTSTORY.STORY  WHERE 	`STATUS` <> 'closed'  	AND PROJECT = t1.`ID`  	AND DELETED = '0'  	) AS `UNCLOSEDSTORYCNT`, 	( SELECT COUNT( 1 ) FROM ZT_BUG WHERE PROJECT = t1.`ID` AND `CONFIRMED` = 0 AND DELETED = '0' ) AS `UNCONFIRMEDBUGCNT`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_TASK  WHERE 	PROJECT = t1.`ID`  	AND `STATUS` NOT IN ( 'done', 'cancel', 'closed' )  	AND DELETED = '0'  	) AS `UNDONETASKCNT`, 	( SELECT 	COUNT( 1 )  FROM 	zt_task t  WHERE 	t.deleted = '0'  	AND t.project = t1.id  	AND t.`status` = 'closed'  	AND t.closedDate BETWEEN CONCAT( 	YEAR ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	MONTH ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	DAY ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	' 00:00:00'  	)  	AND CONCAT( 	YEAR ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	MONTH ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	DAY ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	' 23:59:59'  	)  	) AS `YESTERDAYCTASKCNT`, 	( SELECT 	COUNT( 1 )  FROM 	ZT_BUG  WHERE 	PROJECT = t1.`ID`  	AND `STATUS` = 'resolved'  	AND DELETED = '0'  	AND RESOLVEDDATE BETWEEN CONCAT( 	YEAR ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	MONTH ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	DAY ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	' 00:00:00'  	)  	AND CONCAT( 	YEAR ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	MONTH ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	'-', 	DAY ( DATE_ADD( now( ), INTERVAL - 1 DAY ) ), 	' 23:59:59'  	)  	) AS `YESTERDAYRBUGCNT` , 	(CASE WHEN T2.OBJECTORDER IS NOT NULL THEN T2.OBJECTORDER ELSE  t1.`ORDER` END) as `ORDER1`, 	(CASE WHEN T2.OBJECTORDER IS NOT NULL THEN 1 ELSE 0 END) as `ISTOP` FROM 	`zt_project` t1 	left join t_ibz_top t2 on t1.id = t2.OBJECTID and t2.type = 'project' and t2.ACCOUNT = #{srf.sessioncontext.srfloginname}
			 WHERE 	t1.DELETED = '0'
				 AND ((t1.acl = 'private' and t1.id in (select t3.root from zt_team t3 where t3.account = #{srf.sessioncontext.srfloginname}  and t3.type = 'project')) or t1.acl = 'open')
