mapper.sql:
 SELECT 	t1.account, 	t1.realname, 	t1.`YEAR`, 	CONCAT_WS('',t1.`YEAR`,'-',t1.CURMONTH,'-01 00:00:00') as CURMONTH, 	t1.MONTESTIMATE, 	t11.ss AS MONTRESOLVEDBUG  FROM 	( SELECT 	t1.account, 	t1.realname, 	t11.`YEAR`, 	t11.date AS CURMONTH, 	t11.`consumed` AS MONTESTIMATE  FROM 	zt_user t1 	LEFT JOIN ( SELECT 	t11.account, 	t11.`YEAR`, 	t11.date, 	sum( t11.`consumed` ) AS `consumed`, 	COUNT( t11.task ) AS MONTHFINISHTASK  FROM 	( SELECT   DATE_FORMAT( t11.date, '%Y' ) AS `YEAR`, 	DATE_FORMAT( t11.date, '%m' ) AS date, 	t11.account, 	t11.`consumed`, 	t11.task  FROM 	zt_taskestimate t11  	) t11  WHERE 	t11.date NOT IN ( '00', '' )  GROUP BY 	t11.account, 	t11.`YEAR`, 	t11.date  	) t11 ON t11.account = t1.account  	) t1 	LEFT JOIN ( SELECT 	tt.resolvedBy, 	tt.`YEAR`, 	tt.resolvedDate, 	COUNT( 1 ) AS ss  FROM 	( SELECT DATE_FORMAT( t.resolvedDate, '%Y' ) as `YEAR`,DATE_FORMAT( t.resolvedDate, '%m' ) AS resolvedDate, t.resolvedBy FROM zt_bug t WHERE t.resolvedBy <> '' ) tt  WHERE 	tt.resolvedDate <> '00'  GROUP BY 	tt.resolvedBy, 	tt.`YEAR`, 	tt.resolvedDate  	) t11 ON t1.account = t11.resolvedBy and t1.`YEAR` =  t11.`YEAR` 	AND t11.resolvedDate = t1.CURMONTH
			 WHERE 	t1.`YEAR` = DATE_FORMAT( now(), '%Y' )
				 AND t1.account = #{srf.sessioncontext.srfloginname}
