mapper.sql:
		 SELECT 	t1.`ASSIGNEDTO`, 	t1.`COLOR`, 	t1.`ESTIMATE`, 	t1.`ID`, 	t1.`product`, 	( SELECT 	( CASE WHEN COUNT( t.IBZ_FAVORITESID ) > 0 THEN 1 ELSE 0 END ) AS ISFAVORITES  FROM 	T_IBZ_FAVORITES t  WHERE 	t.TYPE = 'story'  	AND t.ACCOUNT = 'admin'  	AND t.OBJECTID = t1.id  	) AS `ISFAVORITES`, 	t1.`MODULE`, 	t11.`NAME` AS `MODULENAME`, 	t1.`OPENEDBY`, 	t1.`PARENT`, 	( CASE WHEN t1.`PLAN` = '0' THEN '' ELSE t1.plan END ) AS `PLAN`, 	t1.`PRI`, 	t1.`STAGE`, 	t1.`STATUS`, 	t1.`TITLE`, 	t1.STORYSN  FROM 	`zt_story` t1 	LEFT JOIN zt_module t11 ON t1.MODULE = t11.ID  	LEFT JOIN zt_story t21 ON t1.PARENT = t21.ID  	LEFT JOIN zt_product t31 ON t1.PRODUCT = t31.ID  	LEFT JOIN zt_branch t41 ON t1.BRANCH = t41.ID 


                 AND t1.DELETED = '0' 


                 AND  t1.parent = #{srf.datacontext.parent}

		 SELECT t1.`ASSIGNEDDATE`, t1.`ASSIGNEDTO`, t1.ASSIGNEDTO AS `ASSIGNEDTOPK`, t1.`BRANCH`, t41.`NAME` AS `BRANCHNAME`, t1.`CHILDSTORIES`, t1.`CLOSEDBY`, t1.`CLOSEDDATE`, t1.`CLOSEDREASON`, t1.`COLOR`, t1.`DELETED`, t1.`DUPLICATESTORY`, t1.`ESTIMATE`, t1.`FROMBUG`, t1.`ID`, (case when t1.parent in (-1) and t1.stage = 'wait' then '1' when t1.parent in (0) and t1.stage = 'wait' then '2'  else '0' end) AS `ISCHILD`, 0 AS `ISFAVORITES`, ( CASE WHEN t1.parent > 0 THEN TRUE ELSE FALSE END ) AS `ISLEAF`, t1.`KEYWORDS`, t1.`LASTEDITEDBY`, t1.`LASTEDITEDDATE`, t1.`LINKSTORIES`, t1.`MAILTO`, t1.MAILTO AS `MAILTOPK`, t1.`MODULE`, t11.`NAME` AS `MODULENAME`, (case when t1.module = '0' then '/' else (SELECT GROUP_CONCAT( tt.NAME SEPARATOR '>' ) FROM zt_module tt  WHERE FIND_IN_SET( tt.id, t11.path ) AND tt.type = 'story' GROUP BY	tt.root limit 0,1) end) AS `MODULENAME1`, t1.`OPENEDBY`, t1.`OPENEDDATE`, t1.`PARENT`, t21.`TITLE` AS `PARENTNAME`, t11.`PATH`, t1.`PLAN`, t1.`PRI`, t31.`NAME` AS `PRODOCTNAME`, t1.`PRODUCT`, t1.`REVIEWEDBY`, t1.`REVIEWEDDATE`, t1.`SOURCE`, t1.`SOURCENOTE`, t1.`STAGE`, t1.`STAGEDBY`, t1.`STATUS`, t1.`SUBSTATUS`, t1.`TITLE`, t1.`TOBUG`, t1.`TYPE`, t1.`VERSION`, t1.`VERSION` AS `VERSIONC`, t1.STORYSN FROM `zt_story` t1  LEFT JOIN zt_module t11 ON t1.MODULE = t11.id LEFT JOIN zt_story t21 ON t1.PARENT = t21.id LEFT JOIN zt_product t31 ON t1.PRODUCT = t31.id LEFT JOIN zt_branch t41 ON t1.BRANCH = t41.id 


                 AND t1.DELETED = '0' 


                 AND t1.plan <> #{srf.webcontext.plan} 


                 AND t1.product =  #{srf.webcontext.product} 


                 AND t1.`status` <> 'closed' 


                 AND t1.parent <> '-1'
