HEN ( SELECT CASE	 WHEN count( t.`id` ) > 0 THEN 1 ELSE 0  END  FROM `zt_team` t  WHERE t.`type` = 'task'  AND t.`root` = t1.`TASKSN`  ) = 1 THEN '10'  WHEN t1.parent = - 1 THEN'20'   WHEN t1.parent = 0 THEN '30' ELSE '40' END) AS `TASKTYPE`, CONCAT_WS('',case when t1.consumed = 0 or t1.consumed is null then '0' when t1.`left` = 0 or t1.`left` is null then '100' else ROUND((ROUND(t1.`consumed`/(t1.`left` + t1.consumed),2)) * 100) end ,'%') as progressrate, (case when t1.storyVersion < t21.version and t21.`status` <> 'changed' then 'storychange'  else t1.`status` end ) as `STATUS1`, (case when t1.`status` = 'wait' then 10 when t1.`status` = 'doing' then 20 when t1.`status` = 'done' then 30 when t1.`status` = 'closed' then 40 when t1.`status` = 'cancel' then 50 else 60 end) as statusorder, t1.`PLAN`, t1.`TASKSPECIES`, t61.`TITLE` AS `PLANNAME` FROM `zt_task` t1  LEFT JOIN zt_module t11 ON t1.MODULE = t11.MODULESN  LEFT JOIN zt_story t21 ON t1.STORY = t21.STORYSN LEFT JOIN zt_project t31 ON t1.PROJECT = t31.PROJECTSN  LEFT JOIN zt_product t41 ON t21.PRODUCT = t41.productsn  LEFT JOIN zt_task t51 ON t1.PARENT = t51.TASKSN LEFT JOIN `zt_productplan` t61 ON t1.`PLAN` = t61.`PRODUCTPLANSN` 


                 AND t1.DELETED = '0' 


                 AND t1.plan=#{srf.webcontext.plan}

		 SELECT t1.id , t1.`ASSIGNEDDATE`, t1.`ASSIGNEDTO`, t1.`CANCELEDBY`, t1.`CANCELEDDATE`, t1.`CLOSEDBY`, t1.`CLOSEDDATE`, t1.`CLOSEDREASON`, t1.`COLOR`, t1.`CONSUMED`, t1.`DEADLINE`, t1.`DELETED`, (To_Days(t1.`DEADLINE`)-To_Days(t1.`ESTSTARTED`)) AS `DURATION`, t1.`ESTIMATE`, t1.`ESTSTARTED`, t1.`FINISHEDBY`, t1.`FINISHEDDATE`, t1.`FROMBUG`, t1.`TASKSN`, (case when t1.deadline is null or t1.deadline = '0000-00-00' or t1.deadline = '1970-01-01' then '' when t1.`status` in ('wait','doing') and t1.deadline <DATE_FORMAT(now(),'%y-%m-%d') then CONCAT_WS('','延期',TIMESTAMPDIFF(DAY, t1.deadline, now()),'天') else '' end) as DELAY, (select (case when COUNT(t.IBZ_FAVORITESID) > 0 then 1 else 0 end ) as ISFAVORITES from T_IBZ_FAVORITES t where t.TYPE = 'task' and t.ACCOUNT = 'admin' and t.OBJECTID = t1.TASKSN) AS `ISFAVORITES`, ( CASE WHEN t1.parent > 0 THEN TRUE ELSE FALSE END ) AS `ISLEAF`, t1.`LASTEDITEDBY`, t1.`LASTEDITEDDATE`, t1.`LEFT`, t1.`MODULE`, t11.`NAME` AS `MODULENAME`, (SELECT case when tt.type = 'task' then GROUP_CONCAT( tt.NAME SEPARATOR '>' ) else CONCAT_WS('',t2.`name`,'>',GROUP_CONCAT( tt.NAME SEPARATOR '>' )) end as `name` FROM zt_module tt left join zt_product t2 on tt.root = t2.productsn WHERE FIND_IN_SET( tt.id, t11.path ) GROUP BY tt.root LIMIT 0,1) AS `MODULENAME1`, ( SELECT case when count( t.`id` ) > 0 then 1 else 0 end FROM `zt_team` t WHERE t.`type` = 'task' AND t.`root` = t1.`TASKSN` ) AS `MULTIPLE`, t1.`NAME`, t1.`OPENEDBY`, t1.`OPENEDDATE`, t1.`PARENT`, t51.`NAME` AS `PARENTNAME`, t11.`PATH`, t1.`PRI`, t21.`PRODUCT`, t41.`NAME` AS `PRODUCTNAME`, t1.`PROJECT`, t31.`NAME` AS `PROJECTNAME`, t1.`REALSTARTED`, t1.`STATUS`, t1.`STORY`, t21.`TITLE` AS `STORYNAME`, t1.`STORYVERSION`, t1.`SUBSTATUS`, t1.`TYPE`, ( CASE WHEN ( SELECT CASE WHEN count( t.`id` ) > 0 THEN 1 ELSE 0 END FROM `zt_team` t WHERE t.`type` = 'task' AND t.`root` = t1.`TASKSN` ) = 1 THEN '10' WHEN t1.parent = - 1 THEN'20' WHEN t1.parent = 0 THEN '30' ELSE '40' END) AS `TASKTYPE`, CONCAT_WS('',case when t1.consumed = 0 or t1.consumed is null then '0' when t1.`left` = 0 or t1.`left` is null then '100' else ROUND((ROUND(t1.`consumed`/(t1.`left` + t1.consumed),2)) * 100) end ,'%') as progressrate, (case when t1.storyVersion < t21.version and t21.`status` <> 'changed' then 'storychange' else t1.`status` end ) as `STATUS1`, (case when t1.`status` = 'wait' then 10 when t1.`status` = 'doing' then 20 when t1.`status` = 'done' then 30 when t1.`status` = 'closed' then 40 when t1.`status` = 'cancel' then 50 else 60 end) as statusorder , t1.`PLAN`, t1.`TASKSPECIES`, t61.`TITLE` AS `PLANNAME` FROM `zt_task` t1 LEFT JOIN zt_module t11 ON t1.MODULE = t11.MODULESN LEFT JOIN zt_story t21 ON t1.STORY = t21.STORYSN LEFT JOIN zt_project t31 ON t1.PROJECT = t31.PROJECTSN LEFT JOIN zt_product t41 ON t21.PRODUCT = t41.productsn LEFT JOIN zt_task t51 ON t1.PARENT = t51.TASKSN LEFT JOIN `zt_productplan` t61 ON t1.`PLAN` = t61.`PRODUCTPLANSN` 


                 AND t1.deleted='0' 


                 AND t1.project=#{srf.webcontext.project}

		 SELECT 	( SELECT 	MIN( ESTSTARTED )  FROM 	`zt_task`  WHERE 	DELETED = '0'  	AND TYPE = t1.`TYPE`  	AND plan = t1.plan 	AND ESTSTARTED <> '0000-00-00'  	AND estStarted <> '0002-11-30'  	AND estStarted <> '1970-01-01'  	) AS `ESTSTARTED`, 	( SELECT 	MAX( DEADLINE )  FROM 	`zt_task`  WHERE 	DELETED = '0'  	AND TYPE = t1.`TYPE`  	AND plan = t1.plan 	AND DEADLINE <> '0000-00-00'  	AND estStarted <> '0002-11-30'  	AND estStarted <> '1970-01-01'  	) AS `DEADLINE`, 	( SELECT 	DATEDIFF( MAX( DEADLINE ), MIN( ESTSTARTED ) ) + 1  FROM 	`zt_task`  WHERE 	DELETED = '0'  	AND TYPE = t1.`TYPE`  	AND plan = t1.plan 	AND ESTSTARTED <> '0000-00-00'  	AND DEADLINE <> '0000-00-00'  	AND estStarted <> '0002-11-30'  	AND estStarted <> '1970-01-01'  	) AS `DURATION`, 	t1.`TYPE`, 	t1.`PLAN`, 	t1.project 	FROM 		`zt_task` t1 	 	 


