var Delayresolve = '';
var deadline = entity.get("deadline");
deadline = Date.parse(deadline);
var status = entity.get("status");
var resolvedDate = entity.get("resolvedDate");
resolvedDate = Date.parse(resolvedDate);
if(isNaN(deadline) || deadline == Date.parse('0000-00-00') || deadline == Date.parse('1970-01-01')){
	       Delayresolve = '';
	}else if(status == 'resolved' && deadline < resolvedDate){
		var ms= Math.abs(resolvedDate - deadline);
		var days = Math.floor(ms / (24 * 3600 * 1000));
		Delayresolve = ''+'延期'+ days +'天';
	}
		return Delayresolve;
