var Overduebugs = 0;
var deadline = entity.get("deadline");
var end = Date.parse(deadline);
if(end == Date.parse('0000-00-00')){
	Overduebugs = 0;
}else{			
	var now = Date.parse(new Date());
	var ms= Math.abs(end - now);
	 Overduebugs = Math.floor(ms / (24 * 3600 * 1000));
	}
return Overduebugs;
