var end = entity.get("end");
var newdate = new Date();
var delay = ""; 
if(end === null || end === "0000-00-00" || end === "1970-01-01") {
	delay = ""; 
}else {
	var dateSpan;
	end = Date.parse(end);
	dateSpan = Math.abs(end - newdate);
	dateSpan = Math.floor(dateSpan / (24 * 3600 * 1000));
	delay = dateSpan + "天";
}
return delay;
