var end = entity.get("end");
var begin = entity.get("begin");
var duration = 0; 
if(end !== null || begin !== null) {
	var dateSpan;
        end = Date.parse(end);
        begin = Date.parse(begin);
        dateSpan = end - begin;
        dateSpan = Math.abs(dateSpan);
        duration = Math.floor(dateSpan / (24 * 3600 * 1000));
}
return duration;
