var deadline = entity.get("deadline");
var eststarted = entity.get("eststarted");
var duration = 0; 

if(deadline !== null || eststarted !== null) {
	var dateSpan;
        deadline = Date.parse(deadline);
        eststarted = Date.parse(eststarted);
        dateSpan = deadline - eststarted;
        dateSpan = Math.abs(dateSpan);
        duration = Math.floor(dateSpan / (24 * 3600 * 1000));
}
return duration;
