var consumed = entity.get("consumed");
var left = entity.get("left");
var progressrate = "0";
if(consumed === null || consumed == 0 ) {
	progressrate = "0";
}else if(left  === null || left == 0) {
	progressrate = "100";
}else {
	progressrate = (consumed /(consumed  + left) ).toFixed(2) * 100;
}
return progressrate;
